import { useEffect, useState } from "react";

const About = () => {
    const [data, setData] = useState();

    useEffect(() => {
        fetch("/")
            .then((res) => res.json())
            .then((data) => {
                setData(data);
                console.log(data);
            });
    }, []);

    return (
        <div className="about-container">
            <h1 className="about-header">About/Home Page</h1>
            <p className="about-body">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                Phasellus ipsum nunc, dictum eget mollis in, posuere posuere
                purus. Phasellus vitae pellentesque elit, ac consectetur libero.
